package newpack;

public class While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=1,b=35;
		System.out.println("Even numbers between 1 & 35 are : ");
		while(a<b) {
			if(a%2 ==0) {
				System.out.print(a+" ");
			}
			a++;
			
		}

	}

}
