package string;

public class Last_String {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String str="Raj Kapoor";
		int strLastIndex=0;
		
		System.out.println("String is "+str);
		strLastIndex=str.lastIndexOf('o');
		
		System.out.println("Last index of : "+strLastIndex);
		
	}

}
